import { HttpResponse } from '@angular/common/http';
import { ChatService } from '../../services/chat.service';
import { Component } from '@angular/core';


@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.css']
})
export class ChatComponent {
  messages: { sender: string, text: string }[] = [];
  userInput: string = '';
  audioUrl: string | undefined;

  constructor(private chatService: ChatService) { }

  sendMessage() {
    const userMessage = this.userInput;
    if (userMessage) {
      this.messages.push({ sender: 'user', text: userMessage });
      this.userInput = '';

      this.chatService.sendUserMessage(userMessage).subscribe(
        (response) => {
          const assistantMessage = response[0].queryResult.fulfillmentText;
          this.messages.push({ sender: 'assistant', text: assistantMessage });
          /*Convert the outputAudio data to a typed array*/
          const outputAudioData = response[0].outputAudio.data;
          const typedArray = new Uint8Array(outputAudioData);
          /*Create a Blob from the typed array with the appropriate audio MIME type*/
          const blob = new Blob([typedArray], { type: 'audio/wav' });
          /**Create a URL for the Blob object */
          const audioUrl = URL.createObjectURL(blob);
          /**Create a new HTML5 Audio element and set its source to the audio URL */
          const audio = new Audio(audioUrl);
          audio.play();

        },
        error => {
          console.log(error);
        }
      );
    }
  }
}
<script src="https://cdnjs.cloudflare.com/ajax/libs/howler/2.2.3/howler.min.js"></script>

<div class="chat-container">
  <div class="chat-messages">
    <div *ngFor="let message of messages" class="message">

      <div *ngIf="message.sender === 'user'" class="perso">
        <div class="avatar user-avatar">
          <img src="https://cdn.iconscout.com/icon/free/png-256/free-avatar-370-456322.png?f=webp" alt="User Avatar" class="avatar-image">
        </div>
      <div  class="user-message">

        <div class="message-content">{{ message.text }}</div>
      </div>
    </div>
      <div *ngIf="message.sender === 'assistant'" class="perso1">
        <div class="assistant-message" >
        <div class="message-content">{{ message.text }}</div>
       </div>
        <div class="avatar assistant-avatar">
          <img src="https://cdn-icons-png.flaticon.com/512/4712/4712035.png" alt="Assistant Avatar" class="avatar-image">
        </div>
      </div>
    </div>
  </div>
  <div class="chat-input">
    <input type="text" [(ngModel)]="userInput" placeholder="Type your message" class="input-field">
    <button (click)="sendMessage()" class="send-button">Send</button>
    <audio controls *ngIf="audioUrl">
      <source [src]="audioUrl" type="audio/wav">
      Your browser does not support the audio element.
    </audio>
  </div>
</div>
