import { Component } from '@angular/core';
import { InterviewService } from 'src/app/services/interview.service';
import { Interview } from 'src/app/model/interview.model';
import { LoginService } from 'src/app/services/login.service';
@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {
  interviews: {candidatName: string, submitted: string, DateSubmission:string}[] = [];
  username!:string
  constructor(private interviewService: InterviewService, private loginService: LoginService) {

  }
  ngOnInit() {
    this.getInterviews();
  }

  getInterviews() {
    this.interviewService.getInterviews()
      .subscribe(
        (interviews: Interview[]) => {
          console.log(interviews)
          interviews.forEach((intObj: any) => {
            const candidatId = intObj.candidate._id
            this.loginService.getUsername(candidatId).subscribe(
              response => {
                this.username = response.username;
                console.log('Username:', this.username);
              },
              error => {
                console.error('Error retrieving username:', error);
              }
            );
            const candidatName = this.username
            const submitted = intObj.submitted
            const DateSubmission = intObj.createdAt
            this.interviews.push({candidatName, submitted ,DateSubmission})
          });
        },
        (error) => {
          console.error('Error fetching interviews:', error);
        }
      );
  }
}
<h1>Interviews</h1>
<table class="dashboard-table">
    <thead>
        <tr>
            <th>Candidate Name</th>
            <th>Submitted</th>
            <th>Date de creation</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let interview of interviews">
            <td>{{ interview.candidatName }}</td>
            <td>{{ interview.submitted }}</td>
            <td>{{ interview.DateSubmission | date }}</td>
        </tr>
    </tbody>
</table>
